%% Load file to be cropped/analyzed, define name of dataset, set non data numberheightGrid = %fileNameW/OExtension;dataName = %NameOfThisData;nonDataNumber = %NonDataNumber;%% Remove non-dataheightGrid(heightGrid == nonDataNumber) = NaN;%% Plot initial height gridfigure(1);pcolor(heightGrid);shading interp; axis image;title(strcat(dataName, " Height Grid"));xlabel("x Indices");ylabel("y Indices");%% Crop height grid to an axially aligned rectangle of desired size%The following bounds are x and y indices on the height gridxLowerBound = ;xUpperBound = ;yLowerBound = ;yUpperBound = ;heightGrid = heightGrid(yLowerBound:yUpperBound, xLowerBound:xUpperBound);%% Clear unwanted variablesclear 'xLowerBound' 'xUpperBound' 'yLowerBound' 'yUpperBound'%% Plot cropped height gridfigure(2);pcolor(heightGrid);shading interp; axis image;title(strcat(dataName, " Cropped Height Grid"));xlabel("x Indices");ylabel("y Indices");%% Detrend cropped height gridheightGrid = detrend_2d(heightGrid);%% Plot detrended cropped height gridfigure(3);pcolor(heightGrid);shading interp; axis image;title(strcat(dataName, " Detrended Cropped Height Grid"));xlabel("x Indices");ylabel("y Indices");%% Calculate statistics of the sampled area from the height fieldheightMean = mean2(heightGrid);heightMedian = median(median((heightGrid)));heightStDev = std2(heightGrid);heightMax = max(max(heightGrid));heightMin = min(min(heightGrid));disp(["Mean:" heightMean])disp(["Median:" heightMedian])disp(["Standard Deviation:" heightStDev])disp(["Max:" heightMax])disp(["Min:" heightMin])%% Clear statistic variablesclear 'heightMean' 'heightMedian' 'heightStDev' 'heightMax' 'heightMin'%% Plot of cdf values for the detrended cropped height gridfigure(4);cdfplot(reshape(heightGrid,1,[]));title(strcat("CDF Values for the ", dataName, " Detrended Cropped Height Grid"));xlabel("Height");ylabel("CDF Output");%% Save figuresprint('-f1', "HeightGrid", '-djpeg')print('-f2', "CroppedHeightGrid", '-djpeg')print('-f3', "DetrendedCroppedHeightGrid", '-djpeg')print('-f4', "CDFPlot", '-djpeg')