%% load dataload %filepath: /Users/drewwadsworth/documents/"SIP 2017"/grainSizeExport.csvload %filepath: /Users/drewwadsworth/documents/"SIP 2017"/NoFoStDevs.csv%% Make plotsstDevs = NoFoStDevs;%runs down rows, makes full plotsfor j = 1:size(grainSizeExport(:, 1))    style = {'ob', 'xr', 'or', 'xk', 'om', 'xm'};    style = style(1:length(stDevs));    %allows code to run with a smaller dataset    figure(j); clf;        %runs across columns, makes individual points    for i = 1:length(style)        figure(j);        plot(grainSizeExport(j, i), stDevs(i), style(i))                %Making titles and labels specific to each plot        if (j/3 <= 1)            statistic = 'Mean';        elseif (j/3 > 2)            statistic = 'Min';        else            statistic = 'Max';        end                %titles and labels        if (mod(j, 3) == 1)            title(['Standard Deviation of Elevation vs ', statistic, ' of 10th Percentile of Grain Size'])            xlabel([statistic, ' of 10th Percentile of Grain Size for NoFo Sites'])        elseif (mod(j, 3) == 2)            title(['Standard Deviation of Elevation vs ', statistic, ' of 50th Percentile of Grain Size'])            xlabel([statistic, ' of 50th Percentile of Grain Size for NoFo Sites'])        else            title(['Standard Deviation of Elevation vs ', statistic, ' of 90th Percentile of Grain Size'])            xlabel([statistic, ' of 90th Percentile of Grain Size for NoFo Sites'])        end        ylabel('Standard Deviation of Elevation at NoFo Sites')        hold on;    end    legend('NoFo 1', 'NoFo 2', 'NoFo 3', 'NoFo 4', 'NoFo 5', 'NoFo 7')end%% Save Figuresprint('-f1', 'SDVsD10Mean', '-djpeg')print('-f2', 'SDVsD50Mean', '-djpeg')print('-f3', 'SDVsD90Mean', '-djpeg')print('-f4', 'SDVsD10Max', '-djpeg')print('-f5', 'SDVsD50Max', '-djpeg')print('-f6', 'SDVsD90Max', '-djpeg')print('-f7', 'SDVsD10Min', '-djpeg')print('-f8', 'SDVsD50Min', '-djpeg')print('-f9', 'SDVsD90Min', '-djpeg')