%% load dataload /Users/drewwadsworth/documents/"SIP 2017"/grainSizeExport.csvload /Users/drewwadsworth/documents/"SIP 2017"/NoFoStDevs.csvstDevs = NoFoStDevs;for j = 1:size(grainSizeExport(:, 1))    %% Make plots    style = {'ob', 'or', 'xb', 'xk', 'om', 'ok'};    figure(j); clf;    for i = 1:length(style)        figure(j);        currentColor = style(i);        currentGrainSizes = grainSizeExport(j, :);        plot(currentGrainSizes(i), stDevs(i), style(i))        if (j/3 < 1)            statistic = 'Mean';        elseif (j/3 > 2)            statistic = 'Max';        else            statistic = 'Min';        end                if (mod(j, 3) == 1)            title(['Standard Deviation of Elevation vs ', statistic, ' of 10th Percentile of Grain Size'])            xlabel([statistic, ' of 10th Percentile of Grain Size for NoFo Sites'])        elseif (mod(j, 3) == 2)            title(['Standard Deviation of Elevation vs ', statistic, ' of 50th Percentile of Grain Size'])            xlabel([statistic, ' of 50th Percentile of Grain Size for NoFo Sites'])        else            title(['Standard Deviation of Elevation vs ', statistic, ' of 90th Percentile of Grain Size'])            xlabel([statistic, ' of 90th Percentile of Grain Size for NoFo Sites'])        end        ylabel('Standard Deviation of Elevation at NoFo Sites')        hold on;    end    legend('NoFo 1', 'NoFo 2', 'NoFo 3', 'NoFo 4', 'NoFo 5', 'NoFo 7')end